function Spread(t,e){var n=this;n.min=t,n.max=e||1/0,n.isInRange=function(t,e){if(e||0!=e){let r=t/e;return r>=n.min&&r<=n.max}return t>=n.min}}function Payout(t,e,n,r){var a=this;a.shooterCutPercent=t,a.formattedShooterCutPercent=100*t+"%",a.groupCutPercent=e,a.formattedGroupCutPercent=100*e+"%",a.spread=n,a.betAmnt=r,a.calcFinalPotSpread=function(){let t=a.spread.max===1/0?"+":Math.floor(a.spread.max*a.betAmnt());return"$"+Math.ceil(a.spread.min*a.betAmnt())+".00"+("+"===t?t:" - $"+t+".00")},a.calcFinalShooterAmnt=function(t){return"$"+Math.floor(t*a.shooterCutPercent)+".00"},a.calcFinalGroupAmnt=function(t){return"$"+Math.ceil(t*a.groupCutPercent)+".00"}}function PayoutViewModel(){var t=this;t.betAmnt=ko.observable(80),t.finalPot=ko.observable(0),t.spreads={firstTier:new Spread(0,1),secondTier:new Spread(1.01,1.5),thirdTier:new Spread(1.51,2.8),fourthTier:new Spread(2.81)},t.payouts=ko.observableArray([new Payout(0,1,t.spreads.firstTier,t.betAmnt),new Payout(.1,.9,t.spreads.secondTier,t.betAmnt),new Payout(.3,.7,t.spreads.thirdTier,t.betAmnt),new Payout(.5,.5,t.spreads.fourthTier,t.betAmnt)]),t.getFinalSplit=function(){return t.payouts().filter(e=>e.spread.isInRange(t.finalPot(),t.betAmnt()))[0]||null},t.calcShooterCut=function(){let e=t.getFinalSplit();return null===e?0:e.calcFinalShooterAmnt(t.finalPot())},t.calcGroupCut=function(){let e=t.getFinalSplit();return null===e?0:e.calcFinalGroupAmnt(t.finalPot())}}ko.applyBindings(new PayoutViewModel);